--
-- Table structure for table `articles_tags`
--

CREATE TABLE `articles_tags` (
  `id` bigint(20) UNSIGNED NOT NULL,
  `at_article_id` int(11) NOT NULL DEFAULT '0',
  `at_tag_id` int(11) NOT NULL DEFAULT '0'
