(16, 'Mainboard ASUS Z590 ROG MAXIMUS XIII EXTREME', 'mainboard-asus-z590-rog-maximus-xiii-extreme', 24000000, 3, 110, 0, 0, '2021-02-26__250-42754-mainboard-asus-z11pa-d8-dual-cpu-server-amp-workstation-0000-1.jpg', 0, 1, 0, 1, 0, 'Thông số sản phẩm\r\nChipset: Intel Z590\r\nSocket: LGA1200\r\nKích thước: E-ATX\r\nSố khe RAM: 4', '<p>Mainboard ASUS Z590 ROG MAXIMUS XIII EXTREME - l&agrave; bo mạch chủ sử dụng chipset Z590 của Intel thuộc ph&acirc;n kh&uacute;c cao cấp của Asus .Bo mạch chủ n&agrave;y sử dụng socket LGA 1200 c&oacute; thể chạy được CPU Intel Core thế hệ 10 &amp; 11.</p>\r\n\r\n<p>&nbsp;</p>', 0, 0, '2021-02-25 23:15:21', '2021-02-25 23:15:21', NULL),
(17, 'Mainboard ASUS Z590 ROG MAXIMUS XIII HERO', 'mainboard-asus-z590-rog-maximus-xiii-hero', 25000000, 3, 90, 0, 0, '2021-02-26__51.jpg', 0, 1, 0, 1, 0, 'Thông số sản phẩm\r\nBo mạch chủ cao cấp chipset Intel Z590 của Asus\r\nKích thước ATX\r\n4 Khe Ram', '<p>Mainboard ASUS Z590 ROG MAXIMUS XIII HERO - l&agrave; bo mạch chủ sử dụng chipset Z590 của Intel thuộc ph&acirc;n kh&uacute;c cao cấp của Asus .Bo mạch chủ n&agrave;y sử dụng socket LGA 1200 c&oacute; thể chạy được CPU Intel Core thế hệ 10 &amp; 11.</p>', 0, 0, '2021-02-25 23:16:26', '2021-02-25 23:16:26', NULL),
(18, 'Mainboard ASUS ROG STRIX Z590-F GAMING WIFI', 'mainboard-asus-rog-strix-z590-f-gaming-wifi', 29000000, 3, 95, 0, 0, '2021-02-26__250-42754-mainboard-asus-z11pa-d8-dual-cpu-server-amp-workstation-0000-1.jpg', 0, 1, 0, 1, 0, 'Thông số sản phẩm\r\nChipset: Intel Z590\r\nSocket: LGA1200\r\nKích thước: ATX\r\nSố khe RAM: 4', '<p><strong>Mainboard ASUS ROG STRIX Z590-F GAMING WIFI</strong>&nbsp;- l&agrave; bo mạch chủ sử dụng chipset Z590 của Intel thuộc ph&acirc;n kh&uacute;c tầm trung của Asus.Bo mạch chủ n&agrave;y sử dụng socket LGA 1200 c&oacute; thể chạy được CPU Intel Core thế hệ 10 &amp; 11.</p>', 0, 0, '2021-02-25 23:17:47', '2021-02-25 23:17:47', NULL),
(19, 'Card màn hình Asus DUAL RTX 3060-12G-GAMING', 'card-man-hinh-asus-dual-rtx-3060-12g-gaming', 16000000, 3, 100, 0, 0, '2021-02-26__42.jpg', 0, 1, 0, 1, 0, 'Thông số sản phẩm\r\nNhân đồ họa Nvidia RTX 3060\r\nSố nhân Cuda:\r\nXung nhịp GPU tối đa:\r\nBộ nhớ Vram: 12GB GDDR6', '<h2>Đ&aacute;nh gi&aacute; sản phẩm Card m&agrave;n h&igrave;nh Asus DUAL RTX 3060-12G-GAMING Ch&iacute;nh H&atilde;ng</h2>\r\n\r\n<p>Card m&agrave;n h&igrave;nh Asus DUAL RTX 3060-12G-GAMING l&agrave; d&ograve;ng card đồ họa tầm trung của Asus, sử dụng nh&acirc;n đồ họa Nvidia RTX 3060Ti. Đ&acirc;y l&agrave; card đồ họa ph&ugrave; hợp cho nhu cầu chơi game ở độ ph&acirc;n giải 2K.&nbsp;</p>\r\n\r\n<p>Kiến tr&uacute;c Nvidia Ampere</p>\r\n\r\n<p>Bao gồm nh&acirc;n RT thế hệ 2, nh&acirc;n Tensor thế hệ 3 gi&uacute;p hiệu năng tăng vượt trội so với người tiền nhiệm.</p>\r\n\r\n<p>Thiết kế quạt c&ocirc;ng nghệ Axial</p>\r\n\r\n<p>Thiết kế phần trục nhỏ hơn c&ugrave;ng với đ&oacute; l&agrave; tăng độ d&agrave;i c&aacute;nh quạt sau đ&oacute; nối liền với v&ograve;ng tr&ograve;n b&ecirc;n ngo&agrave;i c&ugrave;ng từ đ&oacute; tạo ra &aacute;p lực gi&oacute; tập trung hơn.&nbsp;</p>\r\n\r\n<p>H&igrave;nh thức bắt mắt</p>\r\n\r\n<p>Card m&agrave;n h&igrave;nh Asus DUAL RTX 3060-12G-GAMING sở hữu phần thiết kế b&ecirc;n ngo&agrave;i v&ocirc; c&ugrave;ng bắt mắt.&nbsp;</p>\r\n\r\n<p>Linh kiện cao cấp</p>\r\n\r\n<p>To&agrave;n bộ linh kiện tr&ecirc;n c&aacute;c d&ograve;ng sản phẩm cao cấp của Asus đều được sử dụng linh kiện c&oacute; sự chọn lọc kỹ c&agrave;ng ở cả kh&iacute;a cạnh hiệu năng lẫn độ bền.&nbsp;</p>\r\n\r\n<p>Bứt ph&aacute; hiệu năng dễ d&agrave;ng với GPU Tweak&nbsp;</p>\r\n\r\n<p>Tiện &iacute;ch ASUS GPU Tweak II đưa việc điều chỉnh cạc đồ họa l&ecirc;n một tầm cao mới. N&oacute; cho ph&eacute;p bạn điều chỉnh c&aacute;c th&ocirc;ng số quan trọng bao gồm tốc độ của GPU, bộ nhớ v&agrave; c&agrave;i đặt điện &aacute;p, với t&ugrave;y chọn gi&aacute;m s&aacute;t mọi th&agrave;nh phần trong thời gian thực. Điều khiển quạt n&acirc;ng cao cũng được t&iacute;ch hợp với nhiều t&iacute;nh năng kh&aacute;c để gi&uacute;p bạn tận dụng tối đa card đồ họa của m&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>', 0, 0, '2021-02-25 23:23:15', '2021-02-25 23:24:28', NULL),
(20, 'Card màn hình Asus ROG STRIX RTX 3060-O12G-GAMING', 'card-man-hinh-asus-rog-strix-rtx-3060-o12g-gaming', 19999000, 3, 990, 0, 0, '2021-02-26__51.jpg', 0, 0, 0, 1, 0, 'Thông số sản phẩm\r\nNhân đồ họa Nvidia RTX 3060\r\nSố nhân Cuda:\r\nXung nhịp GPU tối đa:\r\nBộ nhớ Vram: 12GB GDDR6', '<ul>\r\n	<li>&nbsp;TOP 2 Nh&agrave; b&aacute;n lẻ IT lớn nhất Việt Nam</li>\r\n	<li>-&nbsp;<a href=\"https://www.hanoicomputer.vn/chinh-sach-hang-chinh-hang\">Sản phẩm ch&iacute;nh h&atilde;ng 100%</a></li>\r\n	<li>-&nbsp;<a href=\"https://thongtin.hanoicomputer.vn/chinh-sach/chinh-sach-doi-tra-hang/\">Đổi trả h&agrave;ng miễn ph&iacute; trong 3 ng&agrave;y</a></li>\r\n	<li>-&nbsp;<a href=\"https://www.hanoicomputer.vn/huong-dan-mua-hang-tra-gop\">Mua h&agrave;ng trả g&oacute;p l&atilde;i suất 0%</a></li>\r\n	<li>-&nbsp;<a href=\"https://www.hanoicomputer.vn/chinh-sach-bao-hanh\">Trả bảo h&agrave;nh tận nơi sử dụng</a></li>\r\n	<li>-&nbsp;<a href=\"https://www.hanoicomputer.vn/chinh-sach-cho-doanh-nghiep\">Bảo h&agrave;nh tận nơi cho doanh nghiệp</a></li>\r\n	<li>- Bảo h&agrave;nh chuy&ecirc;n nghiệp uy t&iacute;n 20 năm</li>\r\n</ul>', 0, 0, '2021-02-25 23:25:46', '2021-02-25 23:25:46', NULL),
(21, 'Card màn hình ASUS ROG STRIX RTX 3070-O8G-WHITE', 'card-man-hinh-asus-rog-strix-rtx-3070-o8g-white', 24000000, 3, 120, 0, 0, '2021-02-26__51.jpg', 0, 1, 0, 1, 0, 'Thông số sản phẩm\r\nNhân GPU: Nvidia RTX 3070\r\nXung GPU Tối đa: 1755 Mhz\r\nSố nhân Cuda: 5888\r\nDung lượng VRAM: 8Gb GDDR6', '<p><strong>Card m&agrave;n h&igrave;nh ASUS ROG STRIX RTX 3070-O8G-WHITE</strong>&nbsp;l&agrave; một trong những sản phẩm cao cấp nhất của Asus phục vụ cho nhu cầu gaming ở độ ph&acirc;n giải 2K hoặc thậm ch&iacute; l&agrave; 4K. Đ&acirc;y l&agrave;&nbsp;<a href=\"https://www.hanoicomputer.vn/vga-card-man-hinh\" rel=\"noopener\" target=\"_blank\" title=\"card đồ họa\">card đồ họa</a>&nbsp;sử dụng kiến tr&uacute;c Ampare ho&agrave;n to&agrave;n mới c&ugrave;ng nh&acirc;n RT thế hệ 2, nh&acirc;n Tensor thế hệ 3, Nvidia RTX IO, VRAM GDDR6 v&agrave; sản xuất tr&ecirc;n tiến tr&igrave;nh 8nm được Samsung l&agrave;m ri&ecirc;ng.&nbsp;</p>\r\n\r\n<h2>Th&ocirc;ng số kỹ thuật</h2>\r\n\r\n<table style=\"width:980px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p>Sản ph&acirc;̉m</p>\r\n			</td>\r\n			<td>\r\n			<p>VGA - Cạc đ&ocirc;̀ họa</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>T&ecirc;n Hãng</p>\r\n			</td>\r\n			<td>\r\n			<p>ASUS</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Model</p>\r\n			</td>\r\n			<td>\r\n			<p>ROG STRIX RTX 3070-O10G-WHITE</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Engine đồ họa</p>\r\n			</td>\r\n			<td>\r\n			<p>NVIDIA&reg; GeForce RTX&trade;3080</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Bộ nhớ trong</p>\r\n			</td>\r\n			<td>\r\n			<p>8GB</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Kiểu bộ nhớ</p>\r\n			</td>\r\n			<td>\r\n			<p>GDDR6X</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Bus</p>\r\n			</td>\r\n			<td>\r\n			<p>&nbsp;256-Bit</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Engine Clock</td>\r\n			<td>\r\n			<p>OC Mode - 1935 MHz (Boost Clock)</p>\r\n\r\n			<p>Gaming Mode - 1905 MHz (Boost Clock)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Cuda Cores</p>\r\n			</td>\r\n			<td>\r\n			<p>5888</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Memory Speed</p>\r\n			</td>\r\n			<td>\r\n			<p>14 Gbps</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Chuẩn khe cắm</p>\r\n			</td>\r\n			<td>\r\n			<p>PCI Express 4.0 x 16</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Độ ph&acirc;n giải</p>\r\n			</td>\r\n			<td>\r\n			<p>7680x4320</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Cổng giao tiếp</p>\r\n			</td>\r\n			<td>\r\n			<p>DisplayPort 1.4a x 3</p>\r\n\r\n			<p>HDMI 2.1 x 2</p>\r\n\r\n			<p>HDPC 2.3</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 0, 0, '2021-02-25 23:28:23', '2021-02-25 23:28:23', NULL),
(22, 'Card màn hình ASUS ROG STRIX RTX 3080-O10G-WHITE', 'card-man-hinh-asus-rog-strix-rtx-3080-o10g-white', 37000000, 3, 80, 0, 0, '2021-02-26__51.jpg', 0, 0, 0, 1, 0, 'Thông số sản phẩm\r\nNhân GPU: Nvidia RTX 3080\r\nXung GPU Tối đa: 1935 Mhz\r\nSố nhân Cuda: 8704\r\nDung lượng VRAM: 10Gb GDDR6X', '<p><strong>Card m&agrave;n h&igrave;nh ASUS ROG STRIX RTX 3080-O10G-WHITE</strong>&nbsp;l&agrave; một trong những sản phẩm cao cấp nhất của Asus phục vụ cho nhu cầu gaming ở độ ph&acirc;n giải 4K 60Fps. Đ&acirc;y l&agrave;&nbsp;<a href=\"https://www.hanoicomputer.vn/vga-card-man-hinh\" rel=\"noopener\" target=\"_blank\" title=\"card đồ họa\">card đồ họa</a>&nbsp;sử dụng kiến tr&uacute;c Ampare ho&agrave;n to&agrave;n mới c&ugrave;ng nh&acirc;n RT thế hệ 2, nh&acirc;n Tensor thế hệ 3, Nvidia RTX IO, VRAM GDDR6X v&agrave; sản xuất tr&ecirc;n tiến tr&igrave;nh 8nm được Samsung l&agrave;m ri&ecirc;ng.&nbsp;</p>', 0, 0, '2021-02-25 23:29:36', '2021-02-25 23:29:36', NULL),
(23, 'Card màn hình ASUS RTX 3080-10G-EK', 'card-man-hinh-asus-rtx-3080-10g-ek', 19000000, 3, 70, 0, 0, '2021-02-26__42.jpg', 0, 0, 0, 1, 0, 'Thông số sản phẩm\r\nPhiên bản sử dụng Block tản nhiệt nước của EK\r\nNhân GPU: Nvidia RTX 3080\r\nXung GPU Tối đa: OC Mode - 1740 MHz (Boost Clock)\r\nSố nhân Cuda: 8704\r\nDung lượng VRAM: 10Gb GDDR6X', '<p><strong>Card m&agrave;n h&igrave;nh ASUS RTX 3080-10G-EK</strong>&nbsp;l&agrave; một trong những sản phẩm cao cấp nhất của Asus phục vụ cho nhu cầu gaming ở độ ph&acirc;n giải 4K 60Fps. Đ&acirc;y l&agrave;&nbsp;<a href=\"https://www.hanoicomputer.vn/vga-card-man-hinh\" rel=\"noopener\" target=\"_blank\" title=\"card đồ họa\">card đồ họa</a>&nbsp;sử dụng kiến tr&uacute;c Ampare ho&agrave;n to&agrave;n mới c&ugrave;ng nh&acirc;n RT thế hệ 2, nh&acirc;n Tensor thế hệ 3, Nvidia RTX IO, VRAM GDDR6X v&agrave; sản xuất tr&ecirc;n tiến tr&igrave;nh 8nm được Samsung l&agrave;m ri&ecirc;ng.&nbsp;</p>\r\n\r\n<p>Đ&acirc;y l&agrave; phi&ecirc;n bản được t&iacute;ch hợp sẵn block nước của EK, hứa hẹn cho khả năng l&agrave;m m&aacute;t vượt trội so với tản nhiệt kh&iacute; truyền thống.&nbsp;</p>', 0, 0, '2021-02-25 23:30:29', '2021-02-25 23:30:29', NULL),
(24, 'PC GAMING LION 009', 'pc-gaming-lion-009', 8790000, 1, 140, 0, 0, '2021-02-26__250-53835-pcgm220-2.jpg', 0, 0, 0, 1, 0, 'Mainboard:\r\nMainboard ASROCK H410M-HDV\r\nCPU:\r\nCore i3 10100F\r\nRAM:\r\nRAM Desktop PC 8GB 2666MHz Kingston HyperX Fury\r\nVGA (Card đồ họa):\r\nVGA Asus Geforce GTX 1650 PH-GTX1650-O4G 4GB D6 Mới\r\nỔ cứng:\r\nỔ cứng SSD 2.5 Inch 128GB - OSCOOỔ cứng SSD 2.5 Inch 240GB - Kingston A400\r\nNguồn (PSU):\r\nNguồn S88 Power Real - 300W\r\nVỏ Case:\r\nS88 OF1', '<p>ệ thống PC Gaming Lion 009 với những thiết bị phần cứng như CPU Intel Pentium G6400, RAM 8GB, VGA RX 570. Đ&oacute;ng vai tr&ograve; l&agrave; những bộ m&aacute;y gaming cơ bản nhất d&agrave;nh cho người mới bắt đầu của HANOICOMPUTER</p>\r\n\r\n<h3>CHIẾC PC N&Agrave;Y D&Agrave;NH CHO AI?</h3>\r\n\r\n<p>Những người mới bước ch&acirc;n v&agrave;o lĩnh vực giải tr&iacute; tr&ecirc;n m&aacute;y t&iacute;nh, muốn t&igrave;m hiểu về sự tiện lợi của m&aacute;y b&agrave;n trước khi đưa ra quyết định đầu tư d&agrave;i hơi v&agrave;o nền tảng l&agrave;m việc, giải tr&iacute; n&agrave;y.</p>\r\n\r\n<h3>CHI TIẾT CẤU H&Igrave;NH</h3>\r\n\r\n<h4>Intel Pentium Gold G6400&nbsp;</h4>\r\n\r\n<p><em>Điểm s&aacute;ng trong ph&acirc;n kh&uacute;c gi&aacute; rẻ của Intel</em></p>\r\n\r\n<p><strong>Số nh&acirc;n:&nbsp;</strong>2 |&nbsp;<strong>Số luồng:&nbsp;</strong>4 |&nbsp;<strong>Xung nh&acirc;n cơ bản:&nbsp;</strong>3.6GHz&nbsp;|&nbsp;<strong>Xung Turbo:&nbsp;</strong>4.0GHz |&nbsp;<strong>&Eacute;p xung:&nbsp;</strong>Kh&ocirc;ng |&nbsp;<strong>Bộ nhớ đệm:&nbsp;</strong>4MB |&nbsp;<strong>TDP:&nbsp;</strong>65W</p>', 0, 0, '2021-02-25 23:32:45', '2021-02-25 23:32:45', NULL),
(25, 'PC GAMING SHARK 037', 'pc-gaming-shark-037', 12900000, 1, 30, 0, 0, '2021-02-26__250-53835-pcgm220-2.jpg', 0, 0, 0, 1, 0, 'Mainboard: ASUS PRIME H410M-D\r\nCPU: Intel Core i3-10100 (3.6GHz turbo up to 4.3Ghz, 4 nhân 8 luồng, 6MB Cache, 65W)\r\nRAM: RAM CORSAIR Vengeance LPX 8GB (1x8GB) DDR4 2666MHz\r\nSSD: Chưa bao gồm\r\nVGA: Card Gigabyte RX570 GAMING - 4GD', '<p>Hệ thống PC Gaming Shark 037 với những thiết bị phần cứng như CPU i3 10100, VGA RX 570. V&ocirc; địch về hiệu năng trong tầm gi&aacute; chỉ với 10 triệu đồng k&egrave;m với đ&oacute; l&agrave; những lợi &iacute;ch độc nhất v&ocirc; nhị m&agrave; bạn chỉ c&oacute; thể t&igrave;m thấy ở những sản phẩm của HANOICOMPUTER.</p>\r\n\r\n<h3>CHIẾC PC N&Agrave;Y D&Agrave;NH CHO AI?</h3>\r\n\r\n<p>Game thủ e-sport tầm trung muốn try hard, leo rank ở những tựa game cơ bản như DOTA 2, LOL, CS:GO v&agrave; thỉnh thoảng giải tr&iacute; bằng những tựa game offline hạng AAA. Với mức gi&aacute; cực kỳ hấp dẫn m&agrave; hầu như ai cũng c&oacute; thể sở hữu. Đ&acirc;y l&agrave; một trong những &aacute;t chủ b&agrave;i của HANOICOMPUTER để chiếm lĩnh ph&acirc;n kh&uacute;c m&aacute;y lắp r&aacute;p cơ bản.</p>\r\n\r\n<h3>CHI TIẾT CẤU H&Igrave;NH</h3>\r\n\r\n<h4>CPU Intel Core i3 10100</h4>\r\n\r\n<p><em>CPU Gaming cơ bản của Intel</em></p>\r\n\r\n<p><strong>Số nh&acirc;n:&nbsp;</strong>4 |&nbsp;<strong>Số luồng:&nbsp;</strong>8 |&nbsp;<strong>Xung nh&acirc;n cơ bản:&nbsp;</strong>3.6GHz&nbsp;|&nbsp;<strong>Xung Turbo:&nbsp;</strong>4.3GHz |&nbsp;<strong>&Eacute;p xung:&nbsp;</strong>Kh&ocirc;ng |&nbsp;<strong>Bộ nhớ đệm:&nbsp;</strong>6MB |&nbsp;<strong>TDP:&nbsp;</strong>65W</p>', 0, 0, '2021-02-25 23:36:42', '2021-02-25 23:36:59', NULL),
(26, 'PC GAMING FALCON 055', 'pc-gaming-falcon-055', 19000000, 1, 200, 0, 0, '2021-02-26__53077-pcgm188-2.jpg', 0, 0, 0, 1, 0, 'Mainboard: ASUS TUF GAMING B460M-PLUS\r\nCPU: Intel Core i5-10400F (2.9GHz turbo up to 4.3Ghz, 6 nhân 12 luồng, 12MB Cache, 65W)\r\nRAM: Ram Kingston HyperX Fury Black 8GB (1x8GB) DDR4 2666Mhz', '<p>Hệ thống PC Gaming Falcon 055 với những thiết bị phần cứng mới nhất của năm 2021 sẽ l&agrave;m h&agrave;i l&ograve;ng những game thủ đang c&oacute; nhu cầu t&igrave;m mua một chiếc PC tầm trung về giải tr&iacute; v&agrave; l&agrave;m việc.</p>\r\n\r\n<h3>CHIẾC PC N&Agrave;Y D&Agrave;NH CHO AI?</h3>\r\n\r\n<p>Game thủ, người mới chơi PC hướng đến sự đa dụng trong thiết kế, dễ n&acirc;ng cấp qua thời gian v&agrave; kh&ocirc;ng mất c&ocirc;ng suy nghĩ về vấn đề lắp đặt, sửa chữa sau n&agrave;y.</p>\r\n\r\n<h3>CHI TIẾT CẤU H&Igrave;NH</h3>\r\n\r\n<h4>CPU Intel Core i5 10400F</h4>\r\n\r\n<p><em>i5 với mức xung đơn nh&acirc;n cao</em></p>\r\n\r\n<p><strong>Số nh&acirc;n:&nbsp;</strong>6 |&nbsp;<strong>Số luồng:&nbsp;</strong>12 |&nbsp;<strong>Xung nh&acirc;n cơ bản:&nbsp;</strong>3.3GHz&nbsp;|&nbsp;<strong>Xung Turbo:&nbsp;</strong>4.8GHz |&nbsp;<strong>&Eacute;p xung:&nbsp;</strong>Kh&ocirc;ng |&nbsp;<strong>Bộ nhớ đệm:&nbsp;</strong>12MB |&nbsp;<strong>TDP:&nbsp;</strong>65W</p>', 0, 0, '2021-02-25 23:42:50', '2021-02-25 23:42:50', NULL),
(27, 'PC GAMING LION 007', 'pc-gaming-lion-007', 5999000, 1, 40, 0, 0, '2021-02-26__52826-pcws066-sw.png', 0, 0, 0, 1, 0, 'Chủng loại: PC GAMING HANOICOMPUTER\r\nMode: PCGM204\r\nKiểu dáng: Mini Tower\r\nBộ vi xử lý: AMD Ryzen 3 3200G', '<p>Hệ thống PC Gaming Lion 007 với những thiết bị phần cứng như CPU Ryzen 3 3200G, RAM 4GB. Đ&oacute;ng vai tr&ograve; l&agrave; những bộ m&aacute;y gaming cơ bản nhất d&agrave;nh cho người mới bắt đầu của HANOICOMPUTER</p>\r\n\r\n<h3>CHIẾC PC N&Agrave;Y D&Agrave;NH CHO AI?</h3>\r\n\r\n<p>Những người mới bước ch&acirc;n v&agrave;o lĩnh vực giải tr&iacute; tr&ecirc;n m&aacute;y t&iacute;nh, muốn t&igrave;m hiểu về sự tiện lợi của m&aacute;y b&agrave;n trước khi đưa ra quyết định đầu tư d&agrave;i hơi v&agrave;o nền tảng l&agrave;m việc, giải tr&iacute; n&agrave;y.</p>\r\n\r\n<h3>CHI TIẾT CẤU H&Igrave;NH</h3>\r\n\r\n<h4>APU AMD Ryzen 3 3200G</h4>\r\n\r\n<p><em>Giải ph&aacute;p CPU k&egrave;m card đồ hoạ t&iacute;ch hợp mạnh mẽ</em></p>\r\n\r\n<p><strong>Số nh&acirc;n:&nbsp;</strong>4 |&nbsp;<strong>Số luồng:&nbsp;</strong>4 |&nbsp;<strong>Xung nh&acirc;n cơ bản:&nbsp;</strong>3.6GHz&nbsp;|&nbsp;<strong>Xung Turbo:&nbsp;</strong>4.0GHz |&nbsp;<strong>&Eacute;p xung:&nbsp;</strong>C&oacute; |&nbsp;<strong>Bộ nhớ đệm:&nbsp;</strong>6MB |&nbsp;<strong>TDP:&nbsp;</strong>65W</p>', 0, 0, '2021-02-25 23:46:36', '2021-02-25 23:46:36', NULL),
(28, 'PC Lenovo Legion T530-28ICB', 'pc-lenovo-legion-t530-28icb', 9999000, 1, 50, 0, 0, '2021-02-26__56638-pcgm279.jpg', 0, 0, 0, 1, 0, 'Hãng sản xuất: Lenovo\r\nChủng loại: Legion T530\r\nPart Number: 90L300JTVN\r\nKiểu dáng: Tower\r\nBộ vi xử lý: Core i7-9700\r\nChipset: Intel® B360', '<p>D&ograve;ng m&aacute;y chơi game lắp sẵn của Lenovo với cấu h&igrave;nh si&ecirc;u mạnh, vừa đủ cho c&aacute;c game thủ trải nghiệm m&agrave; kh&ocirc;ng cần phải n&acirc;ng cấp th&ecirc;m g&igrave;.</p>\r\n\r\n<h3>Vẻ ngo&agrave;i độc đ&aacute;o</h3>\r\n\r\n<p>Cỗ m&aacute;y chơi game ho&agrave;n hảo, 28L Legion T530 Tower được thiết kế với vẻ ngo&agrave;i đầy phong c&aacute;ch với c&aacute;c th&agrave;nh phần mạnh mẽ của n&oacute;. Một tấm lưới ph&iacute;a trước tinh tế tạo điểm nhấn Raven Black, được l&agrave;m tr&ograve;n bởi hệ thống đ&egrave;n to&agrave;n m&agrave;u đỏ. Thực sự l&agrave; một chiếc PC chơi game ngoạn mục, T530 mang đến cho bạn một thiết kế cải tiến, một bữa tiệc cho đ&ocirc;i mắt.</p>\r\n\r\n<h3>Thiết kế cho tương lai</h3>', 0, 0, '2021-02-25 23:48:35', '2021-02-25 23:48:35', NULL),
(29, 'PC GAMING FALCON 054', 'pc-gaming-falcon-054', 10000000, 1, 110, 0, 0, '2021-02-26__56638-pcgm279.jpg', 0, 0, 0, 1, 0, 'Mainboard: MSI B450 TOMAHAWK MAX\r\nCPU: AMD Ryzen 5 3600 (3.6 GHz Upto 4.2GHz / 32MB / 6 Cores, 12 Threads / 65W / Socket AM4)\r\nRAM: Ram ADATA XPG Spectrix D41 RGB Grey 16GB (2x8GB) 3000MHz', '<h3><strong>Đầu ti&ecirc;n, CPU AMD Ryzen 5 3600 được lựa chọn l&agrave; bộ n&atilde;o cho d&agrave;n m&aacute;y</strong></h3>\r\n\r\n<p>L&agrave; CPU được đ&aacute;nh gi&aacute; l&agrave; sản phẩm Best Selling tr&ecirc;n Amazon.&nbsp; Với 6 nh&acirc;n v&agrave; 12 luồng, Ryzen 5 3600 l&agrave; sản phẩm của AMD l&agrave; sản phẩm được mang ra để so s&aacute;nh với Core i7-9600KF v&agrave; I7-9700KF từ đội xanh Intel. Tần số giữa ba loại được đ&aacute;nh gi&aacute; tương đồng nhau, tuy nhi&ecirc;n AMD c&oacute; TDP l&agrave; 65 W, so với 95 W v&agrave; n&oacute; đi k&egrave;m với hỗ trợ DDR4-3200 với 24 l&agrave;n PCIe 4.0, thay v&igrave; DDR4-2666 v&agrave; 16 l&agrave;n PCIe 3.0. V&agrave; tất nhi&ecirc;n, với mức gi&aacute; rẻ hơn đ&acirc;y l&agrave; lựa chọn tối ưu vừa tối giản được ng&acirc;n s&aacute;ch nhưng vẫn đ&aacute;p ứng tốt về hiệu năng. Kh&ocirc;ng thể phủ nhận rằng c&aacute;c CPU của AMD đang c&agrave;ng ng&agrave;y c&agrave;ng tốt hơn. Amazing g&uacute;t ch&oacute;p cho AMD.</p>', 0, 0, '2021-02-25 23:49:54', '2021-02-25 23:49:54', NULL),
(30, 'PC Asus ROG Huracan G21CN', 'pc-asus-rog-huracan-g21cn', 35000000, 1, 10, 0, 0, '2021-02-26__55669-asset-37.png', 0, 0, 0, 1, 0, 'Chủng loại: ROG Huracan (G21CN)\r\nPart Number: G21CN-D-VN001T\r\nKiểu dáng: Tower\r\nBộ vi xử lý: Intel® Core™ i5-9400F', '<p><strong>ASUS ROG Huracan G21CN-VN001T</strong>&nbsp;l&agrave; một chiếc&nbsp;m&aacute;y t&iacute;nh&nbsp;Gaming mang phong th&aacute;i của một chiến binh c&ocirc;ng nghệ thực thụ với k&iacute;ch thước nhỏ gọn chỉ 13 l&iacute;t, được x&acirc;y dựng dựa tr&ecirc;n ng&ocirc;n ngữ thiết kế viễn tưởng cyber-punk. To&agrave;n bộ khung m&aacute;y được thiết kế g&oacute;c cạnh kết hợp&nbsp;với những đường g&acirc;n được chạm khắc tinh tế đem lại sự mạnh mẽ v&agrave; hầm hố cần thiết. B&ecirc;n h&ocirc;ng phải được trang bị một tấm bảo vệ từ t&iacute;nh (t&ugrave;y chọn phi&ecirc;n bản cấu h&igrave;nh) để tăng cường khả năng tản nhiệt cũng như n&acirc;ng cấp sức mạnh hệ thống.</p>\r\n\r\n<h3><strong>Hiệu năng mạnh mẽ</strong></h3>\r\n\r\n<p>Sở hữu nền tảng xử l&yacute; mạnh mẽ bao gồm: vi xử l&yacute; Intel thế hệ mới nhất, card đồ họa GeForce RTX, ổ lưu trữ SSD M.2 NVMe...&nbsp;<strong>ASUS ROG Huracan</strong></p>', 0, 0, '2021-02-25 23:51:53', '2021-02-25 23:51:53', NULL);
INSERT INTO `products` (`id`, `pro_name`, `pro_slug`, `pro_price`, `pro_category_id`, `pro_number`, `pro_admin_id`, `pro_sale`, `pro_avatar`, `pro_view`, `pro_hot`, `pro_manufacturer_id`, `pro_active`, `pro_pay`, `pro_description`, `pro_content`, `pro_review_total`, `pro_review_star`, `created_at`, `updated_at`, `pro_configuration`) VALUES
(31, 'PC ENTHUSIAST GAMING LIMITED 003', 'pc-enthusiast-gaming-limited-003', 33330000, 1, 6, 0, 0, '2021-02-26__53981-pc-mmo-m004.png', 0, 0, 0, 1, 0, 'Chủng loại: PC GAMING HANOICOMPUTER\r\nModel: PCGM224\r\nKiểu dáng: Mid Tower\r\nBộ vi xử lý: Intel Core i9-10900K\r\nMainboard: ASUS ROG MAXIMUS XII EXTREME', '<p>Hệ thống PC Enthusiast Gaming Limited 003 l&agrave; một trong những bộ m&aacute;y cao cấp nhất m&agrave; HNC đ&atilde; x&acirc;y dựng. Mang trong m&igrave;nh cấu h&igrave;nh phần cứng với CPU i9-10900K, card đồ hoạ RTX 2080Ti v&agrave; 64GB RAM, chiếc m&aacute;y n&agrave;y thừa khả năng xử l&yacute; mọi thứ bạn y&ecirc;u cầu ở đẳng cấp cao nhất.</p>\r\n\r\n<h3>CHIẾC PC N&Agrave;Y D&Agrave;NH CHO AI?</h3>\r\n\r\n<p>Những người c&oacute; khả năng t&agrave;i ch&iacute;nh y&ecirc;u th&iacute;ch thương hiệu ASUS, đặc biệt l&agrave; d&ograve;ng ROG. Muốn trải nghiệm đầy đủ hệ sinh th&aacute;i đỉnh cao cho game thủ m&agrave; c&ocirc;ng ty n&agrave;y mang lại cho người d&ugrave;ng.</p>\r\n\r\n<h3>CHI TIẾT CẤU H&Igrave;NH</h3>\r\n\r\n<h4>CPU Intel Core i9 10900K</h4>\r\n\r\n<p><em>Bộ xử l&yacute; tốt nhất d&agrave;nh cho gaming</em></p>\r\n\r\n<p><strong>Số nh&acirc;n:&nbsp;</strong>10 |&nbsp;<strong>Số luồng:&nbsp;</strong>20 |&nbsp;<strong>Xung nh&acirc;n cơ bản:&nbsp;</strong>3.7GHz&nbsp;|&nbsp;<strong>Xung Turbo:&nbsp;</strong>5.3GHz |&nbsp;<strong>&Eacute;p xung:&nbsp;</strong>C&oacute; |&nbsp;<strong>Bộ nhớ đệm:&nbsp;</strong>20MB |&nbsp;<strong>TDP:&nbsp;</strong>125W</p>\r\n\r\n<h5>Điểm mạnh đ&aacute;ng kể</h5>\r\n\r\n<ul>\r\n	<li>10 nh&acirc;n 20 luồng</li>\r\n	<li>Bộ nhớ đệm dung lượng cao</li>\r\n	<li>HIệu năng đ&atilde; được khẳng định</li>\r\n	<li>Chạy tốt tr&ecirc;n c&aacute;c bo mạch chủ Z490 cao cấp</li>\r\n</ul>', 0, 0, '2021-02-25 23:53:20', '2021-02-25 23:53:20', NULL),
(32, 'PC Asus Gaming Station GS30', 'pc-asus-gaming-station-gs30', 45000000, 1, 19, 0, 0, '2021-02-26__vp21.jpg', 0, 0, 0, 1, 0, 'i9-9900/64GB \r\nRAM (16*4)/256GB \r\nSSD+2TB HDD/RTX2080/K+M/Win 10 Pro) (GS30-9900003B', '<p><strong>PC cao cấp để chơi game v&agrave; s&aacute;ng tạo nội dung&nbsp;</strong>ASUS Gaming Station GS30 l&agrave; một PC cao cấp, pha trộn sức mạnh, độ tin cậy v&agrave; t&iacute;nh năng y&ecirc;u cầu của người s&aacute;ng tạo nội dung chuy&ecirc;n nghiệp với hiệu suất tuyệt đối của m&aacute;y t&iacute;nh để b&agrave;n chơi game chuy&ecirc;n dụng. Được hỗ trợ bởi Bộ xử l&yacute; Intel&reg; Core &trade; i9 thế hệ thứ 9 đi k&egrave;m 64GB RAM v&agrave;&nbsp;đồ họa NVIDIA GeForce RTX2080, Gaming Station GS30 mang đến trải nghiệm kh&ocirc;ng khoan nhượng khi l&agrave;m việc với c&aacute;c ứng dụng y&ecirc;u cầu v&agrave; chơi c&aacute;c tr&ograve; chơi Triple-A mới nhất.</p>\r\n\r\n<p>&nbsp;</p>', 0, 0, '2021-02-25 23:54:23', '2021-02-25 23:54:23', NULL),
(33, 'PC MSI Trident 3', 'pc-msi-trident-3', 19999000, 1, 40, 0, 0, '2021-02-26__vp51.png', 0, 0, 0, 1, 0, 'i5-9400F/8GB RAM/128GB SSD + 1TB HDD/GTX 1660Ti) (9SI-412XVN', '<p>Chiếc PC Gaming c&oacute; k&iacute;ch cỡ chỉ bằng 1 chiếc console nhưng v&ocirc; c&ugrave;ng mạnh mẽ. C&ugrave;ng bạn chinh phục thử th&aacute;ch trong mọi t&igrave;nh huống của cuộc sống.</p>\r\n\r\n<p>Bạn c&oacute; nhớ những ng&agrave;y c&ograve;n trẻ khi bạn mơ ước trở th&agrave;nh anh h&ugrave;ng tr&ecirc;n con đường cứu rỗi nh&acirc;n loại hoặc c&oacute; thể bạn muốn bước v&agrave;o một thế giới giả tưởng, nơi bạn c&oacute; thể l&agrave; một trong những con người rời khỏi tr&aacute;i đất v&agrave; x&acirc;m chiếm c&aacute;c h&agrave;nh tinh kh&aacute;c? C&oacute; lẽ bạn chỉ muốn trở th&agrave;nh người l&iacute;nh giỏi nhất giữa trận chiến đầu v&agrave; giữa thế kỷ 20. M&aacute;y t&iacute;nh để b&agrave;n chơi game của MSI c&oacute; thể gi&uacute;p bạn thực hiện những giấc mơ đ&oacute;.</p>', 0, 0, '2021-02-25 23:55:10', '2021-02-25 23:58:28', NULL),
(34, 'RAM Desktop Gskill Trident Z Neo', 'ram-desktop-gskill-trident-z-neo', 3099000, 3, 100, 0, 0, '2021-02-26__48711-gskill-trident-z-neo.jpg', 0, 1, 0, 1, 0, 'Thông số sản phẩm\r\nDòng sản phẩm Gskill Trident Z Neo mới nhất của Gskill\r\nPhù hợp với nền tảng AMD\r\nDung lượng: 2 x 8GB\r\nThế hệ: DDR4\r\nBus: 3600MHz', '<h2>Th&ocirc;ng số kỹ thuật</h2>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"2\">\r\n			<p>TH&Ocirc;NG TIN CƠ BẢN</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Thương hiệu</p>\r\n			</td>\r\n			<td>\r\n			<p>G.SKILL</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Loại Ram</p>\r\n			</td>\r\n			<td>\r\n			<p>Desktop</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>D&ograve;ng</p>\r\n			</td>\r\n			<td>\r\n			<p>Trident Z Neo Series</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>M&atilde; Part</p>\r\n			</td>\r\n			<td>\r\n			<p>F4-3600C16D-16GTZNC</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\">\r\n			<p>CHI TIẾT</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Dung lượng</p>\r\n			</td>\r\n			<td>\r\n			<p>16GB (2 x 8GB)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Loại</p>\r\n			</td>\r\n			<td>\r\n			<p>DDR4</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Tốc độ</p>\r\n			</td>\r\n			<td>\r\n			<p>3600 MHz</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Độ trễ</p>\r\n			</td>\r\n			<td>\r\n			<p>16</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Hiệu điện thế</p>\r\n			</td>\r\n			<td>\r\n			<p>1.35V</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>ECC</p>\r\n			</td>\r\n			<td>\r\n			<p>Kh&ocirc;ng hỗ trợ</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Đ&oacute;ng g&oacute;i</p>\r\n			</td>\r\n			<td>\r\n			<p>Bộ kit 2 thanh</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>M&agrave;u sắc</p>\r\n			</td>\r\n			<td>\r\n			<p>Bạc/Đen</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Tản nhiệt</p>\r\n			</td>\r\n			<td>\r\n			<p>C&oacute;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>M&agrave;u LED</p>\r\n			</td>\r\n			<td>\r\n			<p>RGB</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 0, 0, '2021-02-26 00:56:30', '2021-02-26 00:56:30', NULL),
(35, 'PC HP ProDesk 400 G6 MT', 'pc-hp-prodesk-400-g6-mt', 9999000, 1, 10, 0, 0, '2021-02-26__vp2.jpg', 0, 0, 0, 1, 0, 'Hãng sản xuất: HP\r\nChủng loại: ProDesk 400 G6\r\nPart Number: 7YH24PA\r\nKiểu dáng/Màu sắc: Tower/Đen\r\nBộ vi xử lý: Core i7-9700', '<p>Được thiết kế để dễ c&agrave;i đặt v&agrave; sử dụng,&nbsp;PC HP ProDesk 400 G6 MT (7YH24PA)&nbsp;cho ph&eacute;p bạn tiết kiệm kh&ocirc;ng gian v&agrave; cung cấp hiệu năng để giải quyết c&ocirc;ng việc suốt cả ng&agrave;y với bộ xử l&yacute; Intel hiệu suất cao thế hệ mới nhất được giải ph&oacute;ng bởi bộ nhớ Intel Optane c&oacute; thể n&acirc;ng cấp t&ugrave;y chọn.</p>', 0, 0, '2021-02-26 01:03:10', '2021-02-26 01:03:10', NULL),
(36, 'Laptop Acer Nitro 5 AN515 55 5206', 'laptop-acer-nitro-5-an515-55-5206', 4500000, 2, 100, 0, 0, '2021-02-26__acer-nitro-5-an515-55-5206-i5-nhq7nsv007-600x600.jpg', 0, 0, 0, 1, 0, 'CPU:	Intel Core i5 Comet Lake, 10300H, 2.50 GHz\r\nRAM:	8 GB, DDR4 (2 khe), 2933 MHz\r\nỔ cứng:	SSD 512 GB M.2 PCIe, Hỗ trợ khe cắm SSD M.2 PCIe, Hỗ trợ khe cắm HDD SATA\r\nMàn hình:	15.6 inch, Full HD (1920 x 1080), 144Hz', '<h3>Thiết kế đậm chất gaming</h3>\r\n\r\n<p>Với t&ocirc;ng m&agrave;u đỏ - đen cực kỳ mạnh mẽ, Acer Nitro 5 đ&atilde; thu h&uacute;t game thủ về ngoại h&igrave;nh đặc sắc của m&igrave;nh với những đường cắt t&aacute;o bạo tại c&aacute;c g&oacute;c cạnh của m&igrave;nh. D&ugrave; hầm hố nhưng trọng lượng của m&aacute;y cũng chỉ&nbsp;<strong>2.3 kg</strong>&nbsp;n&ecirc;n người d&ugrave;ng c&oacute; thể mang theo b&ecirc;n m&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>', 0, 0, '2021-02-26 01:27:26', '2021-02-26 01:27:26', NULL),
(37, 'HDD 2.5 Inch WD Blue 5400rpm WD20SPZX-22UA7T0 - 2TB', 'hdd-25-inch-wd-blue-5400rpm-wd20spzx-22ua7t0-2tb', 2600000, 3, 70, 0, 0, '2021-02-26__6119-1552537273-979-o-cung-hdd-laptop-2tb-western-digital-wd-blue-1.jpg', 0, 0, 0, 1, 0, 'Ổ cứng HDD dung lượng lớn giúp dễ dàng lưu trữ\r\n500GB\r\n1TB\r\n2TB\r\nDung lượng:\r\n2TB\r\nChuẩn kết nối:\r\n2.5 Inch', '<h3>Th&ocirc;ng số kỹ thuật</h3>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Bảo h&agrave;nh:</td>\r\n			<td>2 năm</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Dung lượng:</td>\r\n			<td>2 TB</td>\r\n		</tr>\r\n		<tr>\r\n			<td>B&ocirc;̣ nhớ Cache:</td>\r\n			<td>128 MB</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Tốc độ quay:</td>\r\n			<td>5400 RPM,</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Mức ti&ecirc;u thụ điện:</td>\r\n			<td>1,1W khi nghỉ v&agrave; 2,1W khi tải</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Chuẩn giao tiếp:</td>\r\n			<td>2.5 Inch</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 0, 0, '2021-02-26 01:36:32', '2021-02-26 01:36:32', NULL),
(38, 'Ổ cứng SSD 2.5 Inch 256GB Kingston KC600', 'o-cung-ssd-25-inch-256gb-kingston-kc600', 990000, 3, 4, 0, 0, '2021-02-26__5103-kingston-kc600-3.png', 0, 0, 0, 1, 0, 'Kingston 120GB\r\nKingston 240GB\r\nKingston 480GB\r\nGigabyte 120GB\r\nApacer 120GB\r\nApacer 240GB\r\nApacer 480GB\r\nPlextor 128GB\r\nKingston 256GB (Tốc độ cao, BH 5 năm)\r\nDung lượng:\r\nSSD 256 GB\r\nBảo hành chính hãng 60 tháng', '<p>Ổ cứng SSD 2.5 inch Kingston KC600 sở hữu hiệu suất cực ấn tượng, gi&uacute;p cải thiện tốc độ cho cả dữ liệu n&eacute;n v&agrave; kh&ocirc;ng n&eacute;n. Theo kiểm nghiệm, tốc độ của ổ cứng n&agrave;y cao gấp 15 ổ HDD th&ocirc;ng thường.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ổ cứng SSD 2.5 inch Kingston KC600 được trang bị chip điều khiển của h&atilde;ng SM2259 Silicon Motion nhằm gia tăng tối đa tốc độ giải quyết c&ocirc;ng việc hằng ng&agrave;y cũng như cải thiện năng suất l&agrave;m việc của bạn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ổ cứng SSD Kingston KC600 c&oacute; tốc độ l&ecirc;n đến 550MB/s gi&uacute;p tăng tối đa hiệu năng của m&aacute;y t&iacute;nh, đặc biệt l&agrave; c&aacute;c d&ograve;ng m&aacute;y t&iacute;nh chuy&ecirc;n dụng để l&agrave;m đồ họa hoặc chơi Games. Tăng năng suất l&agrave;m việc cũng như tiết kiệm rất nhiều thời gian cho bạn.</p>', 0, 0, '2021-02-26 01:40:49', '2021-02-26 01:40:49', NULL),
(39, 'RAM laptop Kingston DDR4 bus 2666Mhz 8GB', 'ram-laptop-kingston-ddr4-bus-2666mhz-8gb', 240000, 3, 50, 0, 0, '2021-02-26__4119-screenshot-1.jpg', 0, 0, 0, 1, 0, '4GB - DDR4 bus 2400MHz\r\n8GB - DDR4 bus 2400MHz\r\n16GB - DDR4 bus 2400MHz\r\n4GB - DDR4 bus 2666MHz\r\n8GB - DDR4 bus 2666MHz\r\n16GB - DDR4 bus 2666MHz\r\n8GB - DDR4 bus 3200MHz\r\nDung lượng:\r\n8GB Bus 2666Mhz\r\n36 tháng 1 đổi 1', '<p>Kingston Technology Company, l&agrave; một c&ocirc;ng ty Hoa Kỳ, thuộc sở hữu tư nh&acirc;n, l&agrave; một tập đo&agrave;n c&ocirc;ng nghệ m&aacute;y t&iacute;nh đa quốc gia chuy&ecirc;n về ph&aacute;t triển, sản xuất, b&aacute;n h&agrave;ng v&agrave; hỗ trợ c&aacute;c sản phẩm bộ nhớ flash v&agrave; c&aacute;c sản phẩm bộ nhớ li&ecirc;n quan tới m&aacute;y vi t&iacute;nh kh&aacute;c. Hiện nay Kingston Technology l&agrave; nh&agrave; sản xuất c&aacute;c bộ nhớ DRAM độc lập lớn nhất thế giới, chiếm 46% thị phần DRAM to&agrave;n cầu v&agrave; l&agrave; nh&agrave; cung cấp sản phẩm bộ nhớ flash lớn thứ hai thế giới.</p>\r\n\r\n<p>Kingston phục vụ kh&aacute;ch h&agrave;ng th&ocirc;ng qua một hệ thống mang t&iacute;nh quốc tế bao gồm c&aacute;c nh&agrave; ph&acirc;n phối, c&aacute;c đại l&yacute;, c&aacute;c nh&agrave; b&aacute;n lẻ v&agrave; c&aacute;c kh&aacute;ch h&agrave;ng OEM tr&ecirc;n khắp thế giới, trong đ&oacute; bao gồm Laptop88.</p>', 0, 0, '2021-02-26 01:43:03', '2021-02-26 01:43:03', NULL),
(40, 'Ổ cứng SSD M.2 2280 180GB Intel 540s', 'o-cung-ssd-m2-2280-180gb-intel-540s', 230000, 3, 50, 0, 0, '2021-02-26__4183-ssd-intel-540s-2.png', 0, 0, 0, 1, 0, 'Kích thước M.2 2280. Tốc độ SATA III. Bảo hành chính hãng 36 tháng\r\nKingston M2 2280 128GB\r\nIntel M2 2280 180GB\r\nIntel M2 2280 1TB\r\nIntel NVMe 128GB\r\nSamsung NVMe 128GB\r\nSamsung NVMe 256GB\r\nDung lượng:\r\nSSD 180 GB\r\n✅Bảo hành chính hãng 36 tháng 1 đổi 1', '<p><strong>C&ocirc;ng nghệ ti&ecirc;n tiến nhất</strong></p>\r\n\r\n<p>Ổ cứng SSD Intel 540s Series sử dụng bộ nhớ c&ocirc;ng nghệ Intel&reg; 3D NAND 64 lớp (TLC NAND), d&ograve;ng 540s mang lại sự c&acirc;n bằng giữa hiệu năng v&agrave; gi&aacute; trị. Đi k&egrave;m với c&ocirc;ng nghệ m&atilde; h&oacute;a để bảo mật dữ liệu của bạn với m&atilde; h&oacute;a AES 256-bit gi&uacute;p giữ an to&agrave;n c&aacute;c dữ liệu c&aacute; nh&acirc;n của bạn.</p>\r\n\r\n<p><strong>Độ tin c&acirc;y cực cao &ndash; Ti&ecirc;u thụ điện năng cực thấp</strong></p>\r\n\r\n<p>Với chất lượng v&agrave; độ tin cậy h&agrave;ng đầu thế giới của Intel. Ổ cứng SSD Intel&reg; 540s Series 3D NAND hỗ trợ tự m&atilde; ho&aacute; AES 256-bit v&agrave; được bảo h&agrave;nh l&ecirc;n đến 5 năm, bạn c&oacute; thể ho&agrave;n to&agrave;n tin tưởng v&agrave;o Intel.Ngo&agrave;i ra, ổ cứng SSD Intel&reg; 540s Series tiết kiệm năng lượng l&ecirc;n đến 90% so với một ổ đĩa cứng HDD truyền thống, gi&uacute;p bạn sử dụng laptop được l&acirc;u hơn.</p>', 0, 0, '2021-02-26 01:45:22', '2021-02-26 01:45:22', NULL),
(41, 'Ổ cứng SSD 2.5 Inch 120GB Kingston A400 / SA400', 'o-cung-ssd-25-inch-120gb-kingston-a400-sa400', 650000, 3, 67, 0, 0, '2021-02-26__5533-4013-120-2.png', 0, 0, 0, 1, 0, 'SSD Kingston A400 2.5 Inch\r\nDung lượng 120GB\r\nHàng chính hãng bảo hành 36 tháng 1 đổi 1\r\nKingston 120GB\r\nKingston 240GB\r\nKingston 480GB\r\nGigabyte 120GB\r\nApacer 120GB\r\nApacer 240GB\r\nApacer 480GB\r\nPlextor 128GB\r\nKingston 256GB (Tốc độ cao, BH 5 năm)\r\nDung lượng:\r\n120GB\r\nChuẩn kết nối:\r\n2.5 Inch Sata III\r\n✅ Bảo hành 36 tháng 1 đổi 1', '<p>Kingston l&agrave; c&aacute;i t&ecirc;n kh&ocirc;ng c&ograve;n xa lạ với người d&ugrave;ng c&ocirc;ng nghệ, khẳng định t&ecirc;n tuổi bởi những sản phẩm bộ nhớ độc lập hiệu suất cao, dung lượng lớn v&agrave; độ bền bỉ vượt trội. Thời gian gần đ&acirc;y, h&atilde;ng n&agrave;y đ&atilde; cho ra mắt sản phẩm ổ cứng SSD Kingston A400 với khả năng cải thiện tốc độ xử l&yacute; của m&aacute;y t&iacute;nh nhanh gấp 10 lần những chiếc ổ HDD th&ocirc;ng thường. D&ograve;ng sản phẩm SSD Kingston vốn đ&atilde; bền lại c&oacute; hiệu suất cao n&agrave;y được người d&ugrave;ng đ&aacute;nh gi&aacute; rất cao.</p>', 0, 0, '2021-02-26 01:47:08', '2021-02-26 01:47:08', NULL),
(42, 'RAM Laptop Kingston DDR4 bus 3200MHz - 16GB', 'ram-laptop-kingston-ddr4-bus-3200mhz-16gb', 1800000, 3, 34, 0, 0, '2021-02-26__5819-kvr-ddr4-3200-16gb-01.jpg', 0, 0, 0, 1, 0, 'RAM laptop DDR4\r\nDung lượng 16GB Bus 3200MHz\r\nBảo hành 36 tháng\r\nDung lượng:\r\n16GB\r\nLoại LK:\r\nDDR4\r\nTốc độ:\r\n3200MHz\r\n✅ Bảo hành 36 tháng', '<p><strong>Khả năng tương th&iacute;ch tốt</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ram Kingston DDR4 32000MHz - đ&aacute;p ứng mọi nhu cầu của mọi nền tảng m&aacute;y t&iacute;nh. Tự động nhận dạng c&aacute;c nền tảng được cắm v&agrave;o, kh&ocirc;ng cần phải điều chỉnh bất kỳ thiết lập n&agrave;o trong hệ thống BIOS. V&igrave; vậy, bạn sẽ c&oacute; được hiệu suất cực cao nhờ c&aacute;c c&ocirc;ng nghệ của c&aacute;c bộ xử l&yacute; AMD hoặc c&aacute;c c&ocirc;ng nghệ Intel CPU mới nhất một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Dung lượng lớn</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hỗ trợ dung lượng lưu trữ tối đa l&ecirc;n tới 16GB, cho ph&eacute;p người d&ugrave;ng c&oacute; thể mở nhiều chương tr&igrave;nh/phần mềm c&ugrave;ng một l&uacute;c m&agrave; kh&ocirc;ng sợ xảy ra t&igrave;nh trạng giật lag m&aacute;y t&iacute;nh.</p>\r\n\r\n<p><strong>Tốc độ xử l&yacute; nhanh ch&oacute;ng</strong></p>\r\n\r\n<p>Sử dụng chuẩn bộ nhớ DDR4 với tốc độ bus 3200 Mhz cho ph&eacute;p hệ thống m&aacute;y t&iacute;nh xử l&yacute; nhiều t&aacute;c vụ c&ugrave;ng l&uacute;c m&agrave; kh&ocirc;ng bị chậm m&aacute;y.</p>\r\n\r\n<p>Kh&ocirc;ng những thế, tốc độ truy xuất dữ liệu nhanh ch&oacute;ng, đảm bảo an toàn cho dữ li&ecirc;̣u gi&uacute;p bạn dễ d&agrave;ng thao t&aacute;c v&agrave; sử dụng c&aacute;c chương tr&igrave;nh tr&ecirc;n m&aacute;y t&iacute;nh của m&igrave;nh, r&uacute;t ngắn thời gian thực thi nhanh ch&oacute;ng c&aacute;c t&aacute;c vụ đa nhiệm v&agrave; n&acirc;ng cao hiệu suất c&ocirc;ng việc của bạn.Bền bỉ v&agrave; tuổi thọ l&acirc;u d&agrave;i</p>', 0, 0, '2021-02-26 01:49:18', '2021-02-26 01:49:18', NULL),
(43, 'iMac 27 inch 2011 - Intel Core i5', 'imac-27-inch-2011-intel-core-i5', 14890000, 1, 23, 0, 0, '2021-02-26__4597-imac-5.jpg', 0, 1, 0, 1, 0, '✅Tặng bộ phím chuột không dây S88 KM1W\r\n✅Tặng SSD Oscoo 2.5 256GB\r\n✅Tặng thẻ GoldMember trị giá 3.000.000 đồng\r\nCPU:\r\nQuad-core Intel Core i5 2.7GHz\r\nRAM:\r\n8GB\r\nỔ cứng:\r\nHDD 1TB\r\nCard đồ họa:\r\nAMD Radeon HD 6770M 512MB\r\nMàn hình:\r\n27 inch QHD (2K)\r\n✅Bảo hành 12 tháng', '<p>Rất nhiều người y&ecirc;u th&iacute;ch mẫu iMac 27&quot; 2011 bởi hiệu suất l&agrave;m việc cũng như ngoại h&igrave;nh nổi bật, v&agrave; ở thời điểm n&oacute; ra mắt gần như kh&ocirc;ng c&oacute; đối thủ tr&ecirc;n thị trường? Nhưng mẫu m&aacute;y n&agrave;y ở thời điểm 2019 th&igrave; sao? Nếu bạn c&oacute; thắc về việc mua một m&aacute;y t&iacute;nh được sản xuất từ 2011 c&oacute; thực sự đ&aacute;p ứng hiệu năng c&ocirc;ng việc hiện nay hay kh&ocirc;ng, xin mời bạn theo d&otilde;i b&agrave;i viết n&agrave;y.</p>\r\n\r\n<p><strong>Thiết kế đỉnh cao theo thời gian</strong></p>\r\n\r\n<p>iMac 27&quot; 2011 sở hữu thiết kế b&ecirc;n ngo&agrave;i đều được l&agrave;m bằng vỏ nh&ocirc;m bao quanh, c&ugrave;ng với m&agrave;n h&igrave;nh si&ecirc;u mỏng, mang đến cảm nhận đẳng cấp v&agrave; sang trọng. Thiết kế n&agrave;y thực sự ho&agrave;n hảo v&agrave; gần như được giữ nguy&ecirc;n cho những thế hệ tiếp theo. M&aacute;y cực kỳ th&iacute;ch hợp cho những người kh&ocirc;ng c&oacute; nhu cầu di chuyển nhiều như c&aacute;c nh&agrave; thiết kế đồ hoạ, kỹ sư nghi&ecirc;n cứu&hellip;</p>', 0, 0, '2021-02-26 01:53:05', '2021-02-26 11:54:46', NULL),
(44, 'Máy tính để bàn Acer Aspire XC 885 (Case đồng bộ) - i3 8100, RAM 4GB, HDD 1 TB', 'may-tinh-de-ban-acer-aspire-xc-885-case-dong-bo-i3-8100-ram-4gb-hdd-1-tb', 6000000, 1, 51, 0, 0, '2021-02-26__5163-vi-vn-may-tinh-bo-acer-aspire-xc-885-i3-8100-dtbaqsv008-3.jpg', 0, 0, 0, 1, 0, 'CPU:\r\nIntel Core i3 8100\r\nRAM:\r\nRAM 4GB\r\nỔ cứng:\r\nHDD 1TB\r\nCard đồ họa:\r\nCard Intel HD 630\r\n✅Bảo hành 12 tháng', '<h3>Th&ocirc;ng số kỹ thuật</h3>\r\n\r\n<table style=\"width:394px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>CPU</td>\r\n			<td>Intel Core i3 8100 (&nbsp;3.6 GHz / 6MB / 4 nh&acirc;n, 4 lu&ocirc;̀ng )</td>\r\n		</tr>\r\n		<tr>\r\n			<td>RAM</td>\r\n			<td>4GB</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Card đồ họa</td>\r\n			<td>Intel UHD Graphics 630</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Ổ cứng</td>\r\n			<td>HDD 1 TB&nbsp;&nbsp;7200RPM</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 0, 0, '2021-02-26 01:55:18', '2021-02-26 01:55:18', NULL),
(45, 'Máy tính để bàn / PC Acer Aspire XC 885 (Case đồng bộ) + Kèm màn - i3 8100, RAM 4GB, HDD 1 TB + SSD 128GB, màn Acer 19\"', 'may-tinh-de-ban-pc-acer-aspire-xc-885-case-dong-bo-kem-man-i3-8100-ram-4gb-hdd-1-tb-ssd-128gb-man-acer-19', 6500000, 1, 23, 0, 0, '2021-02-26__5164-acer-eb192q-3-u579-d20161229-t095313-565982.jpg', 0, 0, 0, 1, 0, 'Với thiết kế nhỏ gọn và hiện đại, thích hợp với nhân viên văn phòng\r\nGiá cả phải chăng\r\nCPU:\r\nIntel Core i3 8100\r\nRAM:\r\nRAM 4GB\r\nỔ cứng:\r\nHDD 1TB + SSD 128GB\r\nCard đồ họa:\r\nCard Intel HD 630\r\nMàn hình:\r\nAcer 19 Inch HD\r\n✅Bảo hành 12 tháng\r\n\r\n \r\n\r\n✅ MIỄN PHÍ GIAO HÀNG TẬN NHÀ\r\n\r\n- Với đơn hàng < 4.000.000 đồng: Miễn phí giao hàng cho đơn hàng < 5km tính từ cửa hàng Laptop88 gần nhất\r\n\r\n- Với đơn hàng > 4.000.000 đồng: Miễn phí giao hàng (khách hàng chịu phí bảo hiểm hàng hóa nếu có)', '<p><strong>PC Acer Aspire XC 885 v&agrave; m&agrave;n h&igrave;nh Acer</strong> l&agrave; bộ sản phẩm d&agrave;nh cho sinh vi&ecirc;n, nh&acirc;n vi&ecirc;n văn ph&ograve;ng với thiết kế nhỏ gọn, hiện đại v&agrave; c&oacute; hiệu năng ổn định đ&aacute;p ứng tốt mọi thao t&aacute;c c&ocirc;ng việc v&agrave; giải tr&iacute; cơ bản.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>PC Acer Aspire XC 885</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Ngoại h&igrave;nh nhỏ gọn, hiện đại</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>PC Acer Aspire XC 885 c&oacute; thiết kế nhỏ gọn với k&iacute;ch thước 100mm x 330mm x 295mm (W x D x H), bạn c&oacute; thể đặt tr&ecirc;n b&agrave;n l&agrave;m việc hoặc dưới gầm b&agrave;n vẫn rất dễ d&agrave;ng, thuận lợi kh&ocirc;ng tốn nhiều kh&ocirc;ng gian l&agrave;m việc của bạn. Rất th&iacute;ch hợp cho nh&acirc;n vi&ecirc;n văn ph&ograve;ng v&agrave; những người d&ugrave;ng c&oacute; diện t&iacute;ch l&agrave;m việc hạn chế.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>T&ocirc;ng m&agrave;u chủ đạo l&agrave; m&agrave;u đen tuyền thể hiện sự sang trọng, hiện đại với những đường cắt tinh tế đ&atilde; l&agrave;m cho PC Acer Aspire XC 885 trở th&agrave;nh sự lựa chọn ho&agrave;n hảo với những người th&iacute;ch vẻ đẹp thanh lịch đơn giản.&nbsp;</p>', 0, 0, '2021-02-26 01:58:39', '2021-02-26 01:58:39', NULL),
(46, 'Mac mini (PC mini) Late 2020 (MGNR3SA) - Chip M1 8 Core - SSD 256GB', 'mac-mini-pc-mini-late-2020-mgnr3sa-chip-m1-8-core-ssd-256gb', 17990000, 1, 12, 0, 0, '2021-02-26__5921-18xxx.png', 0, 0, 0, 1, 0, 'Mac mini Late 2020 Chip M1 8 Core\r\nNhỏ gọn, tiện lợi, cấu hình tốt\r\nBảo hành chính hãng 12 tháng\r\nMac Mini Late 2020 - SSD 256GB\r\nMac Mini Late 2020 - SSD 512GB\r\nCPU:\r\nChip M1 8 Core\r\nRAM:\r\nRAM 8GB\r\nỔ cứng:\r\nSSD 256GB\r\nCard đồ họa:\r\nCard 8-core GPU\r\n✅Bảo hành 12 tháng chính hãng Apple Việt Nam\r\n\r\n✅ MIỄN PHÍ GIAO HÀNG TẬN NHÀ\r\n\r\n- Với đơn hàng < 4.000.000 đồng: Miễn phí giao hàng cho đơn hàng < 5km tính từ cửa hàng Laptop88 gần nhất\r\n\r\n- Với đơn hàng > 4.000.000 đồng: Miễn phí giao hàng (khách hàng chịu phí bảo hiểm hàng hóa nếu có)', '<p>Mac mini Late 2020 MGNR3SA - M&aacute;y t&iacute;nh b&agrave;n thu gọn trong l&ograve;ng b&agrave;n tay, nhưng lại mang hiệu năng mạnh mẽ với bộ vi xử l&yacute; chip M1 8Q - con chip mạnh nhất m&agrave; Apple từng tạo ra cho m&aacute;y t&iacute;nh:</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Hiệu suất mạnh mẽ gấp 2.5 lần phi&ecirc;n bản tiền nhiệm</li>\r\n	<li>Hoạt động m&aacute;t mẻ, đảm bảo hiệu năng tốt nhất</li>\r\n	<li>Đầy đủ c&aacute;c cổng kết nối</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Thiết kế si&ecirc;u nhỏ gọn</strong></p>\r\n\r\n<h1>&nbsp;</h1>\r\n\r\n<p>Một trong những điểm hấp dẫn tr&ecirc;n Mac mini ch&iacute;nh l&agrave; thiết kế của n&oacute;. V&igrave; kh&ocirc;ng đi k&egrave;m m&agrave;n h&igrave;nh, b&agrave;n ph&iacute;m v&agrave; c&oacute; ngoại h&igrave;nh nhỏ gọn hết cỡ, n&ecirc;n Mac mini l&agrave; một trong những d&ograve;ng m&aacute;y t&iacute;nh linh hoạt nhất hiện nay. Thiết kế lần n&agrave;y ở Mac mini Late 2020 vẫn được giữ nguy&ecirc;n so với phi&ecirc;n bản tiền nhiệm. To&agrave;n bộ chiếc m&aacute;y Mac mạnh mẽ n&agrave;y chỉ l&agrave; một khối h&igrave;nh vu&ocirc;ng với cạnh chưa đến 20cm v&agrave; độ d&agrave;y vỏn vẹn 3,6cm.&nbsp;Với k&iacute;ch thước n&agrave;y th&igrave; người d&ugrave;ng ho&agrave;n to&agrave;n c&oacute; thể đặt n&oacute; ở tất cả mọi nơi m&agrave; vẫn mang t&iacute;nh thẩm mỹ rất cao.</p>', 0, 0, '2021-02-26 02:00:59', '2021-02-26 02:00:59', NULL),
(47, 'Case máy tính văn phòng S88 VP1-G6400', 'case-may-tinh-van-phong-s88-vp1-g6400', 4470000, 1, 13, 0, 0, '2021-02-26__6148-aaaa.png', 0, 0, 0, 1, 0, 'Case máy tính văn phòng giá rẻ, cấu hình đủ để làm việc, học tập, giải trí nhẹ nhàng. Bền bỉ, hiệu suất tốt, chi phí thấp\r\n(*)Mainboard MB H410:\r\nMainboard ASUS PRIME H410M-KChọn sản phẩm khác \r\n(*)CPU:\r\nCPU Intel Pentium Gold G6400Chọn sản phẩm khác \r\n(*)VGA (Card đồ họa):\r\nCard đồ họa tích hợp theo CPUChọn sản phẩm khác \r\n(*)RAM 1:\r\nRAM Desktop PC 4GB 2666MHz ADATAChọn sản phẩm khác \r\nRAM 2 - Optional:\r\nTùy chọn sản phẩm (không bắt buộc)Chọn sản phẩm khác \r\n(*) Ổ cứng 1:\r\nSSD 2.5 Inch Oscoo - 128GB - Hàng Chính HãngChọn sản phẩm khác \r\nỔ cứng 2 - Optional:\r\nTùy chọn sản phẩm (không bắt buộc)Chọn sản phẩm khác \r\n(*)PSU (nguồn):\r\nNguồn Máy Tính S88 POWER REAL-300WChọn sản phẩm khác \r\n(*) Vỏ Case:\r\nVỏ Case S88 OF1 (Mid Tower - ATX)Chọn sản phẩm khác', '<p><strong>Case m&aacute;y t&iacute;nh văn ph&ograve;ng S88-VP1-G6400 </strong>l&agrave; d&ograve;ng sản phẩm cho sinh vi&ecirc;n, người d&ugrave;ng l&agrave;m văn ph&ograve;ng c&oacute; gi&aacute; rẻ với hiệu năng ổn định v&agrave; thiết kế bền bỉ, ổn định sử dụng l&acirc;u d&agrave;i.&nbsp;</p>\r\n\r\n<ul>\r\n	<li><em><em>Mainboard ASUS PRIME H410M-K + Intel Pentium Gold G6400 xử l&yacute; mọi t&aacute;c vụ, thao t&aacute;c xử l&yacute; c&ocirc;ng việc cơ bản ở văn ph&ograve;ng, học tập của người d&ugrave;ng</em></em></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><em>2 khe RAM để bạn c&oacute; thể dễ d&agrave;ng n&acirc;ng cấp dung lượng, hỗ trợ xử l&yacute; đa nhiệm nhiều việc&nbsp;</em></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><em>Ổ cứng SSD Oscoo 128GB cho kh&ocirc;ng gian lưu trữ dữ liệu rộng lớn&nbsp;</em></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><em>Bộ nguồn S88 Power Real 300W cung cấp nguồn điện đủ để phục vụ c&aacute;c c&ocirc;ng việc cơ bản v&agrave; giải tr&iacute; nhẹ nh&agrave;ng&nbsp;</em></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><em>Vỏ case c&oacute; chất lượng ho&agrave;n thiện tốt, kh&ocirc;ng gian lắp đặt rộng r&atilde;i&nbsp;</em></li>\r\n</ul>', 0, 0, '2021-02-26 02:03:13', '2021-02-26 02:03:13', NULL),
(48, 'Case máy tính văn phòng S88 VP2-G6400', 'case-may-tinh-van-phong-s88-vp2-g6400', 5095000, 1, 10, 0, 0, '2021-02-26__6150-nh-n-i-b-t-pc-vp2.jpg', 0, 1, 0, 1, 0, 'Case máy tính văn phòng giá rẻ, cấu hình đủ để làm việc, học tập, giải trí nhẹ nhàng. Bền bỉ, hiệu suất tốt, chi phí thấp\r\n(*)Mainboard:\r\nMainboard ASUS PRIME H410M-DChọn sản phẩm khác \r\n(*)CPU:\r\nCPU Intel Pentium Gold G6400Chọn sản phẩm khác \r\n(*)VGA (Card đồ họa):\r\nCard đồ họa tích hợp theo CPUChọn sản phẩm khác \r\n(*)RAM 1:\r\nRAM PC (Máy bàn) 8GB Kingston DDR4 bus 2666MHz - Hàng chính hãngChọn sản phẩm khác \r\nRAM 2 - Optional:\r\nTùy chọn sản phẩm (không bắt buộc)Chọn sản phẩm khác \r\n(*)Ổ cứng 1:\r\nỔ cứng SSD M.2 2280 NVMe 128GB Samsung PM971aChọn sản phẩm khác \r\nỔ cứng 2 - Optional:\r\nTùy chọn sản phẩm (không bắt buộc)Chọn sản phẩm khác \r\n(*)Nguồn (PSU):\r\nNguồn Máy Tính S88 POWER REAL-300WChọn sản phẩm khác \r\n(*)Vỏ Case:\r\nVỏ Case S88 OF1 (Mid Tower - ATX)Chọn sản phẩm khác', '<p>&nbsp;</p>\r\n\r\n<p>Case m&aacute;y t&iacute;nh d&agrave;nh cho d&acirc;n ph&ograve;ng ph&ograve;ng gi&aacute; rẻ, bền bỉ, cấu h&igrave;nh ổn định l&agrave;m tốt c&aacute;c t&aacute;c vụ văn ph&ograve;ng mượt m&agrave;, chơi được một số game nhẹ. Dễ d&agrave;ng n&acirc;ng cấp với c&aacute;c t&ugrave;y chọn cấu h&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><em><em>Mainboard ASUS PRIME H410M-D</em></em></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><em>CPU Intel Pentium Gold G6400</em></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><em>Card đồ họa t&iacute;ch hợp theo CPU</em></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><em>RAM PC 8GB Kingston DDR4 bus 2666MHz - H&agrave;ng ch&iacute;nh h&atilde;ng ( C&oacute; 2 khe RAM )</em></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><em>Ổ cứng SSD M.2 2280 NVMe 128GB Samsung PM971a ( 2 Khe ổ cứng )</em></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><em>Nguồn M&aacute;y T&iacute;nh S88 POWER REAL-300W</em></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><em>Vỏ Case S88 OF1 (Mid Tower - ATX)</em></li>\r\n</ul>', 0, 0, '2021-02-26 02:04:50', '2021-02-26 11:46:50', NULL),
(49, 'Laptop Asus ROG ZEPHYRUS G14 GA401IU-HA075T - AMD Ryzen 7', 'laptop-asus-rog-zephyrus-g14-ga401iu-ha075t-amd-ryzen-7', 35000000, 2, 5, 0, 0, '2021-02-26__5669-g14-grey-08.jpg', 0, 0, 0, 1, 0, 'Laptop gaming nhỏ gọn, cao cấp\r\nMàn hình 2K 100% sRGB\r\nBảo hành 24 tháng chính hãng Asus\r\nRyzen 5 + GTX 1650 + màu xám\r\nRyzen 5 + GTX 1650 + màu trắng\r\nRyzen 7 + GTX 1650Ti + màu xám\r\nRyzen 7 + GTX 1650Ti + màu trắng\r\nRyzen 7 + GTX 1650Ti + LED ANIME\r\nRyzen 7 + GTX 1660Ti + màn 2k\r\nRyzen 7 + GTX 1660Ti + màn 2k + LED ANIME\r\nRyzen 7 + GTX 1660Ti + 15.6 inch 144Hz\r\nRyzen 7 + RTX 2060 + 15.6 inch 240Hz\r\nCPU:\r\nAMD Ryzen 7 4800HS\r\nRAM:\r\nRAM 16GB DDR4\r\nỔ cứng:\r\nSSD 512GB NVMe\r\nCard đồ họa:\r\nCard rời Nvidia GTX 1660Ti 6GB\r\nMàn hình:\r\n14 Inch 2K', '<p>Asus ROG ZEPHYRUS G14 GA401IU-HA075T laptop gaming cao cấp mới nhất từ Asus , kh&ocirc;ng chỉ n&acirc;ng cấp về cấu h&igrave;nh m&agrave; c&ograve;n thay đổi ho&agrave;n to&agrave;n về thiết kế:</p>\r\n\r\n<ul>\r\n	<li>Hiệu năng mạnh mẽ với chip Ryzen 7 4800HS c&ugrave;ng card GTX 1660Ti</li>\r\n	<li>Thiết kế hiện đại, mỏng nhẹ</li>\r\n	<li>M&agrave;n h&igrave;nh độ ph&acirc;n giải 2K sắc n&eacute;t</li>\r\n</ul>', 0, 0, '2021-02-26 02:11:50', '2021-02-26 02:11:50', NULL),
(50, 'Laptop MSI GF63 Thin 9SCXR 829VN - Intel Core i5', 'laptop-msi-gf63-thin-9scxr-829vn-intel-core-i5', 20000000, 1, 56, 0, 0, '2021-02-26__5664-gf63-8rd-5.jpg', 0, 0, 0, 1, 0, 'Laptop gaming mỏng nhẹ\r\nCard rời GTX 1650Ti Max-Q 4GB, màn hình 144Hz\r\nBảo hành 12 tháng chính hãng MSI\r\ni5 9300H + GTX 1650Ti + Màn 144Hz\r\ni7 9750H + GTX 1650Ti + Màn 144Hz\r\ni5 10300H + GTX 1650 + Màn 144Hz\r\ni7 10750H + GTX 1650Ti + Màn 144Hz\r\nCPU:\r\nIntel Core i5 9300H\r\nRAM:\r\nRAM 16GB DDR4 (8GB sẵn + 8GB quà tặng)\r\nỔ cứng:\r\nSSD 512GB NVMe\r\nCard đồ họa:\r\nCard rời GTX 1650Ti Max-Q 4GB\r\nMàn hình:\r\n15.6 Inch Full HD 144Hz', '<p>MSI GF63 Thin 9SCSR 829VN được đầu tư nhiều về hiệu năng với chip Intel Core i5 - 9300H, tốc độ xung nhịp từ 2.4 - 4.1 Hz kết hợp với card đồ họa rời GTX 1650Ti gi&uacute;p cho bạn chơi mượt mọi tựa game. M&aacute;y c&ograve;n được trang bị RAM 8GB, bạn c&oacute; thể n&acirc;ng cấp tối đa l&ecirc;n đến 64GB cho ph&eacute;p người d&ugrave;ng lưu trữ tạm thời h&igrave;nh ảnh, video thoải m&aacute;i v&agrave; chạy mọi tựa game một c&aacute;ch nhẹ nh&agrave;ng. Ngo&agrave;i ra, ổ cứng SSD c&oacute; đến 512GB NVMe PCIe hỗ trợ download game ho&agrave;n to&agrave;n nhanh ch&oacute;ng.</p>', 0, 0, '2021-02-26 02:14:17', '2021-02-26 02:14:17', NULL),
(51, 'MacBook Pro 2020 13 inch (MWP42SA/ MWP72SA) - Core i5 2.4GHz - SSD 512GB', 'macbook-pro-2020-13-inch-mwp42sa-mwp72sa-core-i5-24ghz-ssd-512gb', 43000000, 2, 34, 0, 0, '2021-02-26__5661-10046287-macbook-pro-i5-gen-10-13-3-inch-mwp72sa-a-1.jpg', 0, 0, 0, 1, 0, 'Macbook Pro 2020 Core i5 gen 10\r\nMàn hình 13.3 Inch Retina\r\nBảo hành 12 tháng Apple Việt Nam\r\nMacbook Air 2017 - i5 Gen8\r\nMacbook Air 2020 - i3 Gen10\r\nMacbook Air 2020 - i5 Gen10\r\nMacbook Pro 13 2020 - i5 Gen8 - SSD 256GB\r\nMacbook Pro 13 2020 - i5 Gen8 - SSD 512GB\r\nMacbook Pro 13 2020 - i5 Gen10\r\nMacbook Pro 16 2020 - i7 Gen9\r\nCPU:\r\nCore i5 gen 10 2.4Ghz\r\nRAM:\r\nRAM 16GB DDR4\r\nỔ cứng:\r\nSSD 512GB NVMe\r\nCard đồ họa:\r\nCard Intel® Iris® Plus\r\nMàn hình:\r\n13.3 Inch Retina', '<p>Macbook Pro vẫn lu&ocirc;n l&agrave; d&ograve;ng laptop cao cấp được rất nhiều người d&ugrave;ng doanh nh&acirc;n ưa chuộng bởi ngoại h&igrave;nh sang trọng đến từng chi tiết&nbsp; c&ugrave;ng nhiều t&iacute;nh năng hiện đại bật l&ecirc;n đẳng cấp kh&aacute;c biệt. Năm nay, với sự ra mắt của Macbook Pro 13 inch 2020 MWP42SA/MWP72SA, Apple th&ecirc;m một lần nữa l&agrave;m h&agrave;i l&ograve;ng cả những người d&ugrave;ng kh&oacute; t&iacute;nh nhất.&nbsp;</p>\r\n\r\n<p><strong>Tổng quan thiết kế</strong></p>\r\n\r\n<p>Thiết kế của Macbook lu&ocirc;n thỏa m&atilde;n sự mong chờ của kh&aacute;ch h&agrave;ng. Macbook Pro 13 inch 2020 MWP42SA/MWP72SA vẫn sở hữu th&acirc;n h&igrave;nh mỏng nhẹ v&agrave; cao cấp như vẻ ngo&agrave;i vốn c&oacute; tr&ecirc;n c&aacute;c thế hệ tiền nhiệm. Vỏ m&aacute;y được l&agrave;m từ kim loại nh&ocirc;m nguy&ecirc;n khối cực k&igrave; bền bỉ v&agrave; quan trọng l&agrave; chất liệu n&agrave;y tạo n&ecirc;n vẻ ngo&agrave;i v&ocirc; c&ugrave;ng sang trọng cho m&aacute;y. C&ugrave;ng với đ&oacute; l&agrave; logo quen thuộc của h&atilde;ng, h&igrave;nh quả t&aacute;o cắn dở được khắc bằng laser v&ocirc; c&ugrave;ng điệu nghệ, cẩn thận v&agrave; kh&ocirc;ng ph&aacute;t s&aacute;ng như bản 2016 trước nữa.</p>\r\n\r\n<p><strong>Hiệu năng mạnh mẽ</strong></p>\r\n\r\n<p>Những người d&ugrave;ng ưa th&iacute;ch ổ cứng tốc độ si&ecirc;u nhanh tr&ecirc;n Macbook Pro c&oacute; thể sẽ cảm thấy rất hạnh ph&uacute;c trước những n&acirc;ng cấp về dung lượng của chiếc Macbook Pro 13 inch 2020.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Phi&ecirc;n bản Macbook Pro 13 inch 2020 MWP42SA/MWP72SA trang bị ổ cứng SSD 512GB NVMe cho tốc độ khởi động m&aacute;y v&agrave; c&aacute;c ứng dụng nhanh ch&oacute;ng chỉ trong v&agrave;i gi&acirc;y. B&ecirc;n cạnh đ&oacute;,&nbsp; Macbook Pro 13 inch 2020 MWP42SA/MWP72SA trang bị CPU Core i5 gen 10 2.4Ghz c&ugrave;ng RAM 16GB, card đồ họa Intel&reg; Iris&reg; Plus gi&uacute;p người d&ugrave;ng l&agrave;m việc cực mượt m&agrave; tr&ecirc;n chiếc laptop cao cấp n&agrave;y, mọi t&aacute;c vụ chuy&ecirc;n dụng đều được xử l&yacute; trơn tru, đem lại hiệu năng nhanh hơn tới 80% so với thế hệ Macbook Pro&nbsp; tiền nhiệm ở việc chỉnh sửa video 4K v&agrave; chơi game. V&igrave; thế bạn c&oacute; thể thao t&aacute;c với những ứng dụng nặng như render video th&ecirc;m c&aacute;c hiệu ứng đặc biệt; xử l&yacute; bản nhạc đa phương tiện; lập tr&igrave;nh c&aacute;c phần mềm đơn giản &hellip; mọi thứ đều diễn ra nhanh ch&oacute;ng như &yacute; muốn.</p>', 0, 0, '2021-02-26 02:15:47', '2021-02-26 02:58:41', NULL),
(52, 'Laptop HP Envy X360 13-ay0069AU 171N3PA - AMD Ryzen 7', 'laptop-hp-envy-x360-13-ay0069au-171n3pa-amd-ryzen-7', 26000000, 2, 23, 0, 0, '2021-02-26__5655-thumb650-hp-envy-x360-13-ay0067au-convertible-171n1pa-5.jpg', 0, 0, 0, 1, 0, 'Laptop 2 trong 1 nhỏ gọn\r\nChip Ryzen 7 4700U, màn hình Full HD\r\nBảo hành chính hãng 12 tháng\r\nRyzen 5 4500U + 13 inch\r\nRyzen 7 4700U + 13 inch\r\nCPU:\r\nAMD Ryzen 7 4700U\r\nRAM:\r\nRAM 8GB DDR4\r\nỔ cứng:\r\nSSD 256GB NVMe\r\nCard đồ họa:\r\nCard AMD Vega 7\r\nMàn hình:\r\n13.3 Inch Full HD Cảm ứng\r\n✅Bảo hành chính hãng HP Việt Nam 12 tháng', '<p>HP Envy X360 13-ay0069AU 171N3PA l&agrave; chiếc laptop trong mơ của rất nhiều người d&ugrave;ng với thiết kế sang trọng, m&agrave;n h&igrave;nh 2 in 1 xoay gập linh hoạt thuận tiện, hiệu năng mượt m&agrave; kh&ocirc;ng chỉ xử l&yacute; tốt mọi c&ocirc;ng việc văn ph&ograve;ng m&agrave; bạn c&ograve;n c&oacute; thể chơi game phổ th&ocirc;ng tr&ecirc;n chiếc laptop n&agrave;y, l&agrave;m đồ họa 2D nhẹ nh&agrave;ng.&nbsp;</p>\r\n\r\n<p><strong><strong>&nbsp;</strong></strong></p>', 0, 0, '2021-02-26 02:18:06', '2021-02-26 02:18:06', NULL),
(53, 'Laptop Asus ROG ZEPHYRUS M15 GU502LU-AZ006T - Intel Core i7', 'laptop-asus-rog-zephyrus-m15-gu502lu-az006t-intel-core-i7', 10000000, 2, 34, 0, 0, '2021-02-26__5451-asus-rog-zephyrus-m15-gu502lu-az006t-10.jpg', 0, 0, 0, 1, 0, 'Laptop gaming led RGB từng phím\r\nChip i7 đời 10, màn hình 240Hz\r\nBảo hành 24 tháng chính hãng Asus\r\nZephyrus S15\r\nZephyrus S17\r\nCPU:\r\nIntel Core i7 10750H\r\nRAM:\r\nRAM 16GB DDR4\r\nỔ cứng:\r\nSSD 512GB NVMe\r\nCard đồ họa:\r\nCard rời GTX 1660Ti 6GB\r\nMàn hình:\r\n15.6 Inch Full HD 240Hz', '<p>K&iacute;ch thước v&agrave; c&acirc;n nặng ấn tượng tuyệt đối với độ mỏng 18,9mm v&agrave; dưới 1.9kg. Được coi l&agrave; gọn nhẹ trong d&ograve;ng laptop gaming. Zephyrus M15 GU502LU-AZ006T mỏng hơn 25% v&agrave; nhẹ hơn 45% so với laptop 15&Prime; th&ocirc;ng thường. Zephyrus M15 GU502LU-AZ006T ho&agrave;n to&agrave;n c&oacute; thể c&acirc;n bằng giữa c&ocirc;ng việc v&agrave; giải tr&iacute;. Khung m&aacute;y được l&agrave;m từ kim loại. Thiết kế b&ecirc;n ngo&agrave;i b&oacute;ng bẩy chắc chắn B&ecirc;n trong được thiết kế tinh tế nhỏ gọn l&agrave;m cho người sử dụng c&oacute; sự trải nghiệm chuy&ecirc;n nghiệp ở bất cứ đ&acirc;u.</p>\r\n\r\n<p>Asus ROG Zephyrus M15 GU502LU-AZ006T được gia c&ocirc;ng tỉ mỉ cực mỏng đặc biệt vẫn duy tr&igrave; độ bền. Bề ngo&agrave;i được phủ 1 lớp sơn giảm thiểu dấu v&acirc;n tay khi chạm v&agrave;o. C&aacute;c g&oacute;c cạnh được cắt kim cương một c&aacute;ch tinh tế. Logo ROG ph&aacute;t s&aacute;ng m&agrave;u đỏ tạo n&ecirc;n sự tương phản nổi bật của Zephyrus. Nếu bạn l&agrave; t&iacute;n đồ của d&ograve;ng laptop gaming nhưng ưa h&igrave;nh thức th&igrave; đ&acirc;y l&agrave; một sự chọn ho&agrave;n qu&aacute; ho&agrave;n hảo.</p>', 0, 0, '2021-02-26 02:21:08', '2021-02-26 02:21:08', NULL),
(54, 'Laptop Asus ROG STRIX G15 G512L-VAZ068T - Intel Core i7', 'laptop-asus-rog-strix-g15-g512l-vaz068t-intel-core-i7', 35000000, 2, 26, 0, 0, '2021-02-26__5454-asus-rog-strix-g15-g512l-vaz068t-6.jpg', 0, 0, 0, 1, 0, 'Laptop gaming cao cấp, màn hình 15.6\" FHD 240Hz\r\nChip i7 đời 10, card RTX 2060 6GB\r\nBảo hành 24 tháng chính hãng Asus\r\nCore i7 + GTX 1650Ti\r\nCore i7 + GTX 1660Ti\r\nCore i7 + RTX 2070\r\nCore i5 + GTX 1650Ti\r\nCore i7 + RTX 2060\r\n17 inch | Core i7 + GTX 1660Ti\r\n17 inch | Core i7 + RTX 2060\r\nCPU:\r\nIntel Core i7 10750H\r\nRAM:\r\nRAM 16GB DDR4 (2x8GB)\r\nỔ cứng:\r\nSSD 512GB NVMe\r\nCard đồ họa:\r\nCard rời RTX 2060 6GB\r\nMàn hình:\r\n15.6 Inch Full HD 240Hz', '<h1>Asus ROG STRIX G15 G512L-VAZ068T được thiết tối giản, mang đến trải nghiệm cốt l&otilde;i m&atilde;nh liệt với nhiều ưu điểm:&nbsp;</h1>\r\n\r\n<h1>-Hệ thống tản nhiệt th&ocirc;ng minh&nbsp;</h1>\r\n\r\n<p>-M&agrave;n h&igrave;nh tần số qu&eacute;t ấn tượng l&ecirc;n đến 240Hz</p>\r\n\r\n<p>-Hiệu năng mạnh mẽ sẵn s&agrave;ng chinh phục mọi tựa game nặng nhất&nbsp;</p>', 0, 0, '2021-02-26 02:22:24', '2021-02-26 02:22:24', NULL),
(55, 'Laptop HP Pavilion Gaming 15-dk1075TX 1K3V0PA - Intel Core i7', 'laptop-hp-pavilion-gaming-15-dk1075tx-1k3v0pa-intel-core-i7', 27000000, 2, 39, 0, 0, '2021-02-26__5567-hp-pavilion-gaming-15-3.png', 0, 1, 0, 1, 0, 'Laptop gaming cấu hình cao\r\nMàn hình Full HD 144Hz\r\nBảo hành 12 tháng chính hãng HP\r\nRyzen 5 4600H + GTX 1650\r\nRyzen 7 4800H + GTX 1650\r\nCore i5 10300H + GTX 1650\r\nCore i7 10750H + GTX 1650\r\nCore i7 10750H + GTX 1650Ti\r\nCPU:\r\nIntel Core i7 10750H\r\nRAM:\r\nRAM 8GB DDR4\r\nỔ cứng:\r\nSSD 512GB NVMe\r\nCard đồ họa:\r\nCard rời GTX 1650Ti 4GB\r\nMàn hình:\r\n15.6 Inch Full HD 144Hz', '<p>Laptop HP Pavilion Gaming 15-dk1075TX 1K3V0PA l&agrave; d&ograve;ng laptop chuy&ecirc;n dụng d&agrave;nh cho c&aacute;c game thủ của thương hiệu HP. Nằm trong ph&acirc;n kh&uacute;c thị trường gi&aacute; rẻ, sau d&ograve;ng laptop OMEN cao caaso của HP. Đ&acirc;y l&agrave; chiếc laptop nhỏ gọn, mang tr&ecirc;n m&igrave;nh hiệu năng mạnh mẽ từ CPU Intel thế hệ mới nhất v&agrave; Card rời GTX mạnh mẽ gi&uacute;p c&aacute;c game thủ c&oacute; thể tận hưởng với những tựa game đồ họa hot nhất hiện nay.</p>', 0, 0, '2021-02-26 02:23:26', '2021-02-26 11:46:43', NULL),
(56, 'Laptop Acer Nitro 5 2020 AN515-55-5923 - Intel Core i5', 'laptop-acer-nitro-5-2020-an515-55-5923-intel-core-i5', 23000000, 2, 21, 0, 0, '2021-02-26__5575-an515-55-7.jpg', 0, 0, 0, 1, 0, 'Laptop gaming Nitro 5 2020 cấu hình mạnh mẽ\r\nMàn hình Full HD 144Hz\r\nBảo hành chính hãng Acer Việt Nam\r\nTHAM KHẢO NITRO 5 phiên bản AMD MẠNH - MÁT - KHỎE, GIÁ TỐT HƠN: https://bit.ly/2NlvfrV\r\nAN515-44-R9JM - R5 4600H + GTX 1650 + 144hz - RẺ NHẤT\r\nAN515-55-5923 - i5 gen 10 + GTX 1650Ti + 144hz\r\nAN515-55-5304 - i5 gen 10 + GTX 1650Ti\r\nCPU:\r\nIntel Core i5 10300H\r\nRAM:\r\nRAM 8GB DDR4\r\nỔ cứng:\r\nSSD 512GB NVMe\r\nCard đồ họa:\r\nCard rời GTX 1650Ti 4GB\r\nMàn hình:\r\n15.6 Inch Full HD 144Hz', '<p>Acer Nitro 5 AN515-55-5923 - laptop gaming thế hệ mới của Acer với nhiều cải tiến so với thế hệ trước cả về thiết kế lần hiệu năng</p>\r\n\r\n<ul>\r\n	<li>Trang bị đ&egrave;n led RGB 4 v&ugrave;ng với hơn 16 triệu m&agrave;u độc đ&aacute;o&nbsp;</li>\r\n	<li>Bộ vi xử l&yacute; Intel thế hệ thứ 10 mới nhất với hiệu năng mạnh mẽ kết hợp card rời GTX 1650Ti gi&uacute;p khả năng chơi game của chiếc laptop n&agrave;y mạnh mẽ hơn rất nhiều&nbsp;</li>\r\n	<li>M&agrave;n h&igrave;nh 15.6 inch Full HD với tần số qu&eacute;t l&ecirc;n đến 144Hz&nbsp;</li>\r\n</ul>', 0, 0, '2021-02-26 02:24:42', '2021-02-26 02:24:42', NULL),
(57, 'Laptop Lenovo Thinkpad X13 20T2S01E00 - Intel Core i5', 'laptop-lenovo-thinkpad-x13-20t2s01e00-intel-core-i5', 31000000, 1, 15, 0, 0, '2021-02-26__5599-lenovo-thinkpad-x13-gen-1-11.jpg', 0, 1, 0, 1, 0, 'Laptop ThinkPad cao cấp\r\nChip i5 đời 10, màn hình Full HD IPS\r\nBảo hành 36 tháng chính hãng Lenovo\r\nCore i5\r\nCore i7\r\nCPU:\r\nIntel Core i5 10210U\r\nRAM:\r\nRAM 8GB DDR4\r\nỔ cứng:\r\nSSD 512GB NVMe\r\nCard đồ họa:\r\nCard Intel HD 620\r\nMàn hình:\r\n13.3 Inch Full HD', '<p>Nếu t&iacute;nh chất c&ocirc;ng việc của bạn thường xuy&ecirc;n phải di chuyển nhiều, đi c&ocirc;ng t&aacute;c xa hay bạn đang cần một chiếc laptop thật gọn nhẹ nhưng hiệu năng mạnh mẽ để c&oacute; thể đ&aacute;p ứng mọi c&ocirc;ng việc văn ph&ograve;ng mỗi ng&agrave;y th&igrave; Lenovo Thinkpad X13 20T2S01E00 sẽ l&agrave; một sự lựa chọn l&yacute; tưởng cho bạn</p>\r\n\r\n<ul>\r\n	<li><strong><strong>&nbsp;</strong></strong><strong>Sở hữu ngoại h&igrave;nh si&ecirc;u nhỏ gọn với m&agrave;n h&igrave;nh 13.3 Inch Full HD, trọng lượng chỉ tầm 1.2 kg</strong></li>\r\n	<li>Trang bị cấu h&igrave;nh cao với CPU đời 10 mới nhất hiện nay</li>\r\n	<li>B&agrave;n ph&iacute;m trải nghiệm g&otilde; sướng, kh&ocirc;ng g&acirc;y mỏi tay ngay cả khi bạn soạn thảo văn bản trong nhiều giờ</li>\r\n</ul>', 0, 0, '2021-02-26 02:26:00', '2021-02-28 03:26:55', NULL);

